import React from "react";
import { Handle } from "react-flow-renderer";

import Dropdown from "./Dropdown";

const BooleanOperations = {
  Union: "Union",
  Difference: "Difference",
  Intersection: "Intersection",
};

export default function BooleanNode({ data, id }) {
  const [operation, setOperation] = React.useState(BooleanOperations.Union);

  return (
    <div className="custom-node">
      <div className="custom-node-header">Boolean</div>
      <Dropdown
        value={operation}
        onChange={setOperation}
        items={[
          BooleanOperations.Union,
          BooleanOperations.Difference,
          BooleanOperations.Intersection,
        ]}
        label="Operation"
      />

      <Handle
        type="target"
        target="0"
        id={0}
        style={{left:"30%"}}
        position="top"
        onConnect={(params) => console.log("handle onConnect", params)}
      />
      <Handle
        type="target"
        target="0"
        id={1}
        style={{left:"60%"}}
        position="top"
        onConnect={(params) => data.updateBooleanPrimitive(params.source, params.target)}
      />
      <Handle
        type="target"
        target="0"
        id={2}
        style={{left:"50%"}}
        position="bottom"
        onConnect={(params) => console.log("handle onConnect", params)}
      />
    </div>
  );
}
