import React, { useState, useEfect } from "react";
import Grid from '@mui/material/Grid';
import Divider from '@mui/material/Divider';
import TextField from '@mui/material/TextField';
import InputAdornment from '@mui/material/InputAdornment';
import newId from '../uniqueIdHook';

function PolynomialInput(props) {
    return (
        <TextField
            sx={{ width: '100%' }}
            value={props.val}
            onChange={props.handleChange}
            id={props.label}
            error={false}
            helperText={""}
            InputProps={{
                startAdornment: (
                    <InputAdornment position="start">{props.label} = </InputAdornment>
                ),
            }}
        />
    )
}

export default function GrobnerPage() {
    const [dividendo, setDividendo] = useState("");
    const [divisores, setDivisores] = useState(["2x", "3x"]);

    return <Grid
        container

        rowSpacing={10}
        padding={2}
    >

        <Grid item xs={6}>

            {divisores.map(function (div, idx) {
                return (
                    <div key={newId('dividerInput_')}>
                        <Grid item xs={12}>
                            <PolynomialInput label={`f${idx + 1}`} val={divisores[idx]} />
                        </Grid>
                    </div>
                );
            })}

        </Grid>
        <Grid item xs={6}>TODO</Grid>
    </Grid>;
}
