export const sphere = (radius) => `
    length(p)-${parseFloat(radius).toFixed(1)}
`;

export const box = (sx,sy,sz) => `
    length( 
        max(
            vec3(abs(p.x)-${sx}, abs(p.y)-sy, abs(p.z)-${sz}), 
            0.0
        ) 
    ) 
    + 
    min(
        max( 
            abs(p.x)-${sx}, 
            max(abs(p.y)-sy, abs(p.z)-${sz}) 
        ),
        0.0
    )
`;
