import React from "react";
import { sphere, box } from "./primitives";
import Shader from "./Shader.js";
import { Dropdown } from "rsuite";

import { fs } from "./fragmentShader.js";

export default function PrimitiveNode({ data }) {
    const [primitive, setPrimitive] = React.useState(sphere());

    const handleChange = (val, ev) =>{
        console.log(val);
    }
  return (
    <div className="custom-node">
      <div className="custom-node-header">Primitive</div>
      <Dropdown title="Primitive">
        <Dropdown.Item onSelect={()=>setPrimitive(sphere(1.0))}>Sphere</Dropdown.Item>
        <Dropdown.Item onSelect={setPrimitive(box(1.0, 1.0,1.0))}>Box</Dropdown.Item>
      </Dropdown>
      <Shader
        shader={fs(sphere(1))}
        uniforms={{ color: { type: "3fv", value: [1.0, 1.0, 0.0] } }}
        style={{ margin: "10px" }}
      />
    </div>
  );
}
