import React from "react";
import { useCallback, useState, useEffect } from "react";
import ReactFlow, {
  addEdge,
  applyEdgeChanges,
  applyNodeChanges,
  MarkerType,
  useNodesState,
  useEdgesState,
} from "react-flow-renderer";
import ButtonEdge from './ButtonEdge.js';
import ShaderNode from "./ShaderNode.js";

import "./styles.css";

const initialNodes = [
  {
    id: "node-1",
    type: "shaderNode",
    position: { x: 0, y: 0 },
    data: { value: 123 }
  },
  {
    id: "node-2",
    type: "shaderNode",
    position: { x: 250, y: 0 },
    data: { value: 1 }
  },
  {
    id: "node-3",
    type: "shaderNode",
    position: { x: 250, y: -300},
    data: { value: 1 }
  }
];

const initialEdges = [

];

const nodeTypes = { shaderNode: ShaderNode };
const edgeTypes = { buttonEdge: ButtonEdge };

export default function Graph() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  // no funciona: borra todos
  const onRemoveEdge = (id) =>{
    console.log("INTENTAS BORRAR " + id);
    console.log(edges);
    var s = edges.find(obj => {
      return obj.id === id;
    })
    console.log(s);
    // setEdges(edges.splice(id, 1));
  }

  const onConnect = useCallback(
    (params) => setEdges(
      (eds) => addEdge(
        { 
          ...params, 
          animated:true, 
          markerEnd: { type: 'arrow', color: '#f00' }, 
          type: 'buttonEdge', 
          data: {onRemoveEdge: onRemoveEdge} 
        }, 
        eds)
      ),
    []
  );

  useEffect(() => {
    console.log(edges);
  }, [edges]);

  return (
    <div style={{height: "100vh"}}>
      <ReactFlow
        nodes={nodes}
        edges={edges}

        onConnect={onConnect}
        // onDisconnect={onDisconnect}

        nodeTypes={nodeTypes}
        edgeTypes={edgeTypes}

        snapToGrid={true}
        fitView
      />
    </div>
  );
}
