import React from "react";
import { createTheme, ThemeProvider, styled } from '@mui/material/styles';

import {
  Header,
  Content,
  Footer,
  Navbar,
  Nav,
  Icon,
  Panel,
  Grid,
  Row, Col,
} from "rsuite";

import Graph from "./Graph";
import MaterialsPage from './MaterialsPage';
import "rsuite/dist/styles/rsuite-default.css";

import "./styles.css";

import AppBar from 'material-ui/AppBar';
import Tabs, { Tab } from 'material-ui/Tabs';
import Toolbar from 'material-ui/Toolbar';
import Typography from 'material-ui/Typography';
import Shader from "./CustomComponents/Shader";
import {fs} from "./ShaderStuff/fragmentShaderMovable";

const theme = createTheme({
  palette: {
    primitive: '#609dff',
    boolean: '#ff5858',
    deform: '#3ec224',
    transform: '#ffbb62'
  },
});




function MainContent(){ 
  return(
    <>
    <Grid fluid>
      <Row>
        <Col xs={6}>
          <Shader 
            style={{height: "100vh"}}
            shader={fs("box(p, vec3(1.0))")} 
            uniforms={{ color: { type: "3fv", value: [1.0, 1.0, 0.0] } }}
          />
        </Col>
        <Col xs={1}>
          <div style={{borderRight: "2px solid grey",  height: "100vh"}} />
        </Col>
        <Col xs={17}>
          {/* <ContextMenu/> */}
          
          <Graph/>
        </Col>
      </Row>
      
    </Grid>
    </>
  );
}

const layoutStyles = {
  display: "flex",
  flexDirection: "column",
  height: "100vh"
};

export default function App() {
  const NavBarExample = () => {
    return (
      <AppBar component="div" position="static">
        <Toolbar>
          <Grid container alignItems="center" justify="space-between">
            <Grid item>
              <Typography color="inherit" variant="title">Header</Typography>
            </Grid>
            <Grid item>
              <Tabs classes={classes} onChange={this.changeTab} value={currentTab}>
                <Tab classes={classes} icon={<Settings />} component={Link} to="/a/" value="/a/" />
                <Tab classes={classes} icon={<Settings />} component={Link} to="/b/" value="/b/" />
                <Tab classes={classes} icon={<Settings />} component={Link} to="/c/" value="/c/" />
              </Tabs>
            </Grid>
          </Grid>
        </Toolbar>
      </AppBar>
    );
  };
  
  return (
    <div style={layoutStyles}>
      <ThemeProvider theme={theme}>
        <Header>
          <NavBarExample />
        </Header>
        <Content style={{ flexGrow: 1 }}>
          <MainContent />
        </Content>
        <Footer>
          <Panel bordered>
            <p>Daniel Zufr√≠ Quesada</p>
          </Panel>
        </Footer>
      </ThemeProvider>
    </div>
  );
}
