import React from "react";
import { Handle } from "react-flow-renderer";
import { useTheme } from '@mui/material/styles';

const Triangle = ({ w = '20', h = '20', direction = 'right', stroke="black" , color = '#44a6e8' }) => {
  const points = {
    top: [`${w / 2},0`, `0,${h}`, `${w},${h}`],
    right: [`0,0`, `0,${h}`, `${w},${h / 2}`],
    bottom: [`0,0`, `${w},0`, `${w / 2},${h}`],
    left: [`${w},0`, `${w},${h}`, `0,${h / 2}`],
  }

  return (
    <svg width={w} height={h}>
      <polygon points={points[direction].join(' ')} fill={color} />
      Sorry, your browser does not support inline SVG.
    </svg>
  )
}

export default function CustomHandle(props) {
  const theme = useTheme();
  const radius = 10;
  const margin = 2;

  const style = {
    ...props.style, width: `${radius}px`, height: `${radius}px`,
    background: `${theme.palette.primary.light}`,
    border: `1px solid ${theme.palette.primary.main}`
  }



  return (
    <>
      {props.type === "source" ?
        <Handle
          type="source"
          id={props.id}
          style={{
            ...style, marginRight: `-${margin}px`
          }}
          position="right"
        >
          <Triangle w={radius} h={radius} color={theme.palette.primary.main}/>
          </Handle>
        :
        <Handle
          type="target"
          id={props.id ? props.id : "0"}
          style={{
            ...style, marginLeft: `-${margin}px`
          }}
          position="left"
        />
      }
    </>
  );
}
