import React from "react";
import { sphere, box } from "./primitives";
import Shader from "./Shader.js";
import { Dropdown } from "rsuite";
import {
  Handle
} from "react-flow-renderer";
import { fs } from "./fragmentShader.js";

export default function PrimitiveNode({ data }) {
    const [primitive, setPrimitive] = React.useState(sphere(1.0));

    const handleChange = (val, ev) =>{
        console.log(val);
    }
  return (
    <div className="custom-node">
      <div className="custom-node-header">Primitive</div>
      <Dropdown title="Primitive">
        <Dropdown.Item onSelect={()=>setPrimitive(sphere(1.0))}>Sphere</Dropdown.Item>
        <Dropdown.Item onSelect={()=>setPrimitive(box(1.0, 1.0,1.0))}>Box</Dropdown.Item>
      </Dropdown>
      
      <div className="custom-node-subheader custom-node-subheader__inputs">
        
      <div className="custom-node-port-in">{"Inputs"}</div>
        <div className="custom-node-port custom-node-port-in">
          label
          <Handle
            className="circle-port circle-port-left"
            type="target"
            target="0"
            id={0}
            position="left"
            onConnect={(params) => console.log('handle onConnect', params)}
          />
          </div>
        </div>
      <Shader
        shader={fs(primitive)}
        uniforms={{ color: { type: "3fv", value: [1.0, 1.0, 0.0] } }}
        style={{ margin: "10px" }}
      />
    </div>
  );
}
