import React from "react";
import { useCallback, useEffect } from "react";
import ReactFlow, {
  addEdge,
  Background,
  useNodesState,
  useEdgesState,
} from "react-flow-renderer";
import ButtonEdge from './ButtonEdge.js';
import ShaderNode from "./ShaderNode.js";

import "./styles.css";

const initialNodes = [
  {
    id: "node-1",
    type: "shaderNode",
    position: { x: 0, y: 0 },
    data: { value: 123 }
  },
  {
    id: "node-2",
    type: "shaderNode",
    position: { x: 250, y: 0 },
    data: { value: 1 }
  },
  {
    id: "node-3",
    type: "shaderNode",
    position: { x: 250, y: -300},
    data: { value: 1 }
  }
];

const initialEdges = [

];

const nodeTypes = { shaderNode: ShaderNode };
const edgeTypes = { buttonEdge: ButtonEdge };

const onInit = (reactFlowInstance) => console.log('flow loaded:', reactFlowInstance);

export default function Graph() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  // no funciona: borra todos
  const onRemoveEdge = (id) =>{
    console.log(edges);
    setEdges(edges.filter(edge => edge.id == id));
    onEdgesChange(edges);
  }

  const onConnect = useCallback(
    (params) => setEdges(
      (eds) => addEdge(
        { 
          ...params, 
          animated:true, 
          markerEnd: { type: 'arrow', color: '#f00' }, 
          type: 'buttonEdge', 
          data: {onRemoveEdge: onRemoveEdge} 
        }, 
        eds)
      ),
    []
  );

  useEffect(() => {
    console.log("ASASA");
    console.log(edges);
  }, [edges]);

  const newNode = () =>{
    return {
      id: `node-${nodes.length}`,
      type: "shaderNode",
      position: { x: 0, y: 0 },
      data: { value: 1 }
    }
  }

  const handleKey= (e) => {
    // SPACE BAR
    if (e.key === " ") {
      const node = newNode();
      nodes.push(node);

      onNodesChange([node]);  // Para actualizar
    }

  };

  return (
    <div style={{height: "100vh"}} tabIndex="0" onKeyDown={handleKey}>
      <ReactFlow
        nodes={nodes}
        edges={edges}

        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        // onDisconnect={onDisconnect}

        nodeTypes={nodeTypes}
        edgeTypes={edgeTypes}

        onInit={onInit}
        onEdgeClick={(ev, edge)=>console.log(edge)}

        snapToGrid={true}
        fitView
      >
        <Background color="#aaa" gap={10} />
        </ReactFlow>
    </div>
  );
}
