import { useRef } from 'react'

let uniqueId = 0;
const getUniqueId = () => uniqueId++;

export default function useUniqueId() {
  const idRef = useRef(null);
  if (idRef.current === null) {
    idRef.current = getUniqueId()
  }
  return idRef.current;
}
