import React, { useState, useEffect } from 'react';
import SurfaceCard from './SurfaceCard';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';


import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Button from '@mui/material/Button';
import EquationEditor from "equation-editor-react";


function SurfaceDialog(props) {
    const [equation, setEquation] = useState("y=x");

    useEffect(() => {
        let fn = require(evaluatex(equation));
        let result = fn({ x: 3 });
        console.log("EVALUACION EN X=3: " + result);
    }, [equation]);

    return(
        <Dialog open={props.open} onClose={props.handleClose}>
        <DialogTitle>New Surface</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Type implicit equation of the surface:
          </DialogContentText>
          {equation}
          <EquationEditor
          style={{width: "100%", borderColor: "red"}}
      value={equation}
      onChange={setEquation}
      autoCommands="pi theta sqrt sum prod alpha beta gamma rho"
      autoOperatorNames="sin cos tan"
    />
        </DialogContent>
        <DialogActions>
          <Button onClick={props.handleClose}>Cancel</Button>
          <Button onClick={props.handleClose}>Subscribe</Button>
        </DialogActions>
      </Dialog>
    )
}
export default function SurfacePage() {
    const [dialogOpen, setDialogOpen] = useState(false);

  return (
    <Box>
      <Fab
        style={{ position: 'absolute', bottom: 16, right: 16 }}
        color="primary"
        aria-label="add"
        onClick={()=>setDialogOpen(true)}
      >
        <AddIcon />
      </Fab>
      <SurfaceDialog open={dialogOpen} handleClose={()=>setDialogOpen(false)} />
      <Grid
        container
        direction="row"
        justifyContent="flex-start"
        alignItems="flex-start"
        spacing={2}
      >
        <Grid item>
          <SurfaceCard />
        </Grid>
        <Grid item>
          <SurfaceCard />
        </Grid>
        <Grid item>
          <SurfaceCard />
        </Grid>
        <Grid item>
          <SurfaceCard />
        </Grid>
        <Grid item>
          <SurfaceCard />
        </Grid>
        <Grid item>
          <SurfaceCard />
        </Grid>
        <Grid item>
          <SurfaceCard />
        </Grid>
      </Grid>
    </Box>
  );
}
