import React from "react";
import { useCallback, useEffect } from "react";
import ReactFlow, {
  addEdge,
  Background,
  useNodesState,
  useEdgesState,
  MarkerType,
} from "react-flow-renderer";
import PrimitiveNode from "./PrimitiveNode.js";
import BooleanNode from "./BooleanNode.js";
import ButtonEdge from "./ButtonEdge";
import ShaderNode from "./ShaderNode.js";
import TestNode from "./TestNode.js";

import "./styles.css";
import { GraphProvider } from "./GraphContext.js";

const nodeTypes = { primitiveNode: PrimitiveNode, booleanNode: BooleanNode };
const edgeTypes = { buttonEdge: ButtonEdge };

const initialNodes = [
  {
    id: `node-0`,
    type: "primitiveNode",
    position: { x: 0, y: -150 },
    data: { inputs: {}, sdf: "", children: [] },
  },
  {
    id: `node-1`,
    type: "booleanNode",
    position: { x: 0, y: 350 },
    data: { inputs: {}, sdf: "", children: [] },
  },
  {
    id: `node-2`,
    type: "booleanNode",
    position: { x: 0, y: 700 },
    data: { inputs: {}, sdf: "", children: [] },
  }
];

const initialEdges = [];

const onInit = (reactFlowInstance) =>
  console.log("flow loaded:", reactFlowInstance);

export default function Graph() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [id, setId] = React.useState(3);

  const updateNodeSdf = (id, newSdf, parent) => {
    console.log("ACTUALIZADONDO SDF DE " + id);
    var foundNode = null;

    var nodesCopy = [...nodes];

    nodesCopy.forEach(node => {
      if(node.id === id){
        node.data = {
          ...node.data,
          sdf: newSdf,
        };

        nodesCopy.forEach(n => {
          node.data.children.forEach(child => {
            if(child === n.id){
              console.log("\tACTUALIZANDO INPUTS DE HIJO " + child);
              var newInputs = n.data.inputs;
              newInputs[`${node.id}`] = newSdf;

              n.data = {
                ...node.data,
                inputs: newInputs,
              };
            }
          });
        })
      }

      return node;
    });

    // setNodes((nds) =>
    //   nds.map((node) => {
    //     if (node.id === id) {
    //       node.data = {
    //         ...node.data,
    //         sdf: newSdf,
    //       };

    //       console.log("HIJOS DE " + id + ": ");
    //       console.log(node.data.children);

    //       node.data.children.forEach((child) => {
    //         console.log("\tACTUALIZANDO HIJO " + child);
    //         var newInputs = child.data.inputs;
    //         newInputs[`${node.id}`] = newSdf;

    //         child.data = {
    //           ...node.data,
    //           inputs: newInputs,
    //         };
    //       });
    //     }

    //     return node;
    //   })
    // );

    console.log("enco");
    console.log(foundNode);
  };

  const removeChild = (parent, child) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === parent) {
          console.log(`ELIMINANDO HIJO ${child} DE ${parent}`);

          var newChildren = node.data.children;
          newChildren = newChildren.filter(function (c) {
            return c.id != child;
          });

          node.data = {
            ...node.data,
            children: newChildren,
          };
        }
        if (node.id === child) {
          console.log(`ACTUALIZANDO ${child}`);
          var newInputs = node.data.inputs;
          newInputs[`${parent}`] = "";
          node.data = {
            ...node.data,
            inputs: newInputs,
          };
        }

        onNodesChange([node]);
        return node;
      })
    );
  };

  const removeEdge = (edgeId) => {
      console.log("WDGES: ");
    console.log(edges);
     const edge = edges.find(e => e.id === edgeId);

     var newEdges = edges.filter((edge) => {
       console.log(edge.source);
       return edge.id != edgeId;
     });

    setEdges(newEdges);
     removeChild(edge.source, edge.target);
  };

  const sharedFunctions = {
    updateNodeSdf,
    removeEdge
  };

  const onConnect = (params) => {
    const sourceNode = nodes.find((n) => n.id === params.source);

    // CREAMOS EDGE
    setEdges((eds) =>
      addEdge(
        {
          ...params,
          type: "buttonEdge",
          animated: true,
          markerEnd: { type: MarkerType.Arrow, color: "#000" },
        },
        eds
      )
    );

    // AÑADIMOS HIJO AL PADRE
    var newChildren = sourceNode.data.children;
    newChildren.push(params.target);

    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === params.source) {
          console.log(`AÑADIENDO HIJO ${params.target} A ${params.source}`);

          // var newChildren = node.data.children;
          // console.log(typeof newChildren);
          // console.log(newChildren);
          // newChildren.push(params.target);
          console.log(newChildren);
          node.data = {
            ...node.data,
            children: newChildren,
          };
        }
        if (node.id === params.target) {
          console.log(`INICIALIZANDO ${params.source}`);
          var newInputs = node.data.inputs;
          newInputs[`${params.source}`] = sourceNode.data.sdf;
          node.data = {
            ...node.data,
            inputs: newInputs,
          };
        }

        onNodesChange([node]);
        return node;
      })
    );

    // INICIAMOS HIJO
  };

  useEffect(() => {
    console.log("NODOS ACTUALIZADOS");
    console.log(nodes);
  }, [nodes]);

  useEffect(() => {
    console.log("NUEVO EDGE:");
    console.log(edges);
  }, [edges]);

  const newPrimitiveNode = (xPos = 0, yPos = 0) => {
    setId(id + 1);
    const nodeId = `node-${id}`;
    return {
      id: nodeId,
      type: "primitiveNode",
      position: { x: xPos, y: yPos },
      data: {
        inputs: {},
        sdf: "",
        children: [],
      },
    };
  };

  const newBooleanNode = (xPos = 0, yPos = 0) => {
    setId(id + 1);
    return {
      id: `node-${id}`,
      type: "booleanNode",
      position: { x: xPos, y: yPos },
      data: { inputs: {}, sdf: "", children: [] },
    };
  };

  const handleKey = (e) => {
    // SPACE BAR
    if (e.key.toLowerCase() === "p") {
      const node = newPrimitiveNode();
      nodes.push(node);

      onNodesChange([node]); // Para actualizar
    } else if (e.key.toLowerCase() === "b") {
      const node = newBooleanNode();
      nodes.push(node);

      onNodesChange([node]); // Para actualizar
    }
  };

  return (
    <div style={{ height: "100vh" }} tabIndex="0" onKeyDown={handleKey}>
      <GraphProvider value={sharedFunctions}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          // onDisconnect={onDisconnect}

          nodeTypes={nodeTypes}
          edgeTypes={edgeTypes}
          onInit={onInit}
          // onEdgeClick={(ev, edge) =>
          //   setEdges(edges.filter((e) => e.id != edge.id))
          // }
          snapToGrid={true}
          fitView
        >
          <Background color="#aaa" gap={10} />
        </ReactFlow>
      </GraphProvider>
    </div>
  );
}
