import React from "react";

import {
  Header,
  Content,
  Footer,
  Navbar,
  Nav,
  Icon,
  Placeholder,
  Panel,
  Grid

} from "rsuite";

import Graph from "./Graph";
import "rsuite/dist/styles/rsuite-default.css";

import "./styles.css";
import Shader from "./Shader";
import {fs} from "./fragmentShaderMovable";

const { Paragraph } = Placeholder;

const NavBarExample = () => {
  return (
    <Navbar appearance="inverse">
      <Navbar.Header>
        <a className="navbar-brand logo">3D Visualizer</a>
      </Navbar.Header>
      <Navbar.Body>
        <Nav>
          <Nav.Item eventKey="1">Tab 1</Nav.Item>
          <Nav.Item eventKey="2">Tab 2</Nav.Item>
          <Nav.Item eventKey="3">Tab 3</Nav.Item>
        </Nav>
        <Nav pullRight>
          <Nav.Item icon={<Icon icon="cog" />}>Settings</Nav.Item>
        </Nav>
      </Navbar.Body>
    </Navbar>
  );
};

const stepsStyles = {
  width: "200px",
  display: "inline-table",
  verticalAlign: "top"
};

const StepsExample = () => (
  <div>
    <Graph />
  </div>
);

function MainContent(){ 
  return(
    <Grid fluid>
      <Row className="show-grid">
      <Col xs={24} sm={24} md={8}>
        xs={24} sm={24} md={8}
      </Col>
      <Col xs={24} sm={24} md={8}>
        xs={24} sm={24} md={8}
      </Col>
      <Col xs={24} sm={24} md={8}>
        xs={24} sm={24} md={8}
      </Col>
    </Row>

    <Row className="show-grid">
      <Col sm={24} md={8} lg={6}>
        sm={24} md={8}
      </Col>
      <Col sm={12} md={8} lg={12}>
        sm={12} md={6} lg={12}
      </Col>
      <Col sm={12} md={8} lg={6}>
        sm={12} md={8}
      </Col>
    </Row>
      <Shader 
        style={{width: "100px"}}
        shader={fs()} 
        uniforms={{ color: { type: "3fv", value: [1.0, 1.0, 0.0] } }}
      />
      <Graph />
    </Grid>
  );
}

const layoutStyles = {
  display: "flex",
  flexDirection: "column",
  height: "100vh"
};

export default function App() {
  return (
    <div style={layoutStyles}>
      <Header>
        <NavBarExample />
      </Header>
      <Content style={{ flexGrow: 1 }}>
        <MainContent />
      </Content>
      <Footer>
        <Panel bordered>
          <p>Daniel Zufr√≠ Quesada</p>
        </Panel>
      </Footer>
    </div>
  );
}
