import React from "react";

import {
  Header,
  Content,
  Footer,
  Navbar,
  Nav,
  Icon,
  Placeholder,
  Panel
} from "rsuite";

import Graph from "./Graph";
import "rsuite/dist/styles/rsuite-default.css";

import "./styles.css";
import Shader from "./Shader";
import {fs} from "./fragmentShaderMovable";

const { Paragraph } = Placeholder;

const NavBarExample = () => {
  return (
    <Navbar appearance="inverse">
      <Navbar.Header>
        <a className="navbar-brand logo">3D Visualizer</a>
      </Navbar.Header>
      <Navbar.Body>
        <Nav>
          <Nav.Item eventKey="1">Home</Nav.Item>
          <Nav.Item eventKey="2">News</Nav.Item>
          <Nav.Item eventKey="3">Products</Nav.Item>
        </Nav>
        <Nav pullRight>
          <Nav.Item icon={<Icon icon="cog" />}>Settings</Nav.Item>
        </Nav>
      </Navbar.Body>
    </Navbar>
  );
};

const stepsStyles = {
  width: "200px",
  display: "inline-table",
  verticalAlign: "top"
};

const StepsExample = () => (
  <div>
    <Graph />
  </div>
);

const MainContent = () => <><Shader style={{width: "100px"}}shader={fs()} uniforms={{ color: { type: "3fv", value: [1.0, 1.0, 0.0] } }}/><Graph /></>;

const layoutStyles = {
  display: "flex",
  flexDirection: "column",
  height: "100vh"
};

export default function App() {
  return (
    <div style={layoutStyles}>
      <Header>
        <NavBarExample />
      </Header>
      <Content style={{ flexGrow: 1 }}>
        <MainContent />
      </Content>
      <Footer>
        <Panel bordered>
          <p>Daniel Zufr√≠ Quesada</p>
        </Panel>
      </Footer>
    </div>
  );
}
