import React, { useState, useEffect } from 'react';
import Grid from '@mui/material/Grid';
import InputAdornment from '@mui/material/InputAdornment';

import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Button from '@mui/material/Button';
import MathJax from 'react-mathjax';
import 'katex/dist/katex.min.css';
import useLocalStorage from '../storageHook.js';
import usePrimitivesHook from './primitivesHook';

export default function SurfaceDialog(props) {
    var nerdamer = require('nerdamer');
    require('nerdamer/Calculus');
  
    const [eqData, setEqData] = useState(props.eqData ? props.eqData : {});

    const [eqInput, setEqInput] = useState('');
    const [nameInput, setNameInput] = useState('');

    const [parsedEq, setParsedEq] = useState('');
    
    const [storage, setStorage] = useLocalStorage('user_implicits', {});
    const [primitives, setPrimitives] = usePrimitivesHook();

    const [errorMsg, setErrorMsg] = useState('');
    const [validEq, setValEq] = useState(false);
    const [validName, setValidName] = useState(false);
  
    const traverseTree = (node) => {
      if (node) {
        if (node.type === 'VARIABLE_OR_LITERAL') {
          console.log('VARIABLE: ' + node.value);
          const isVariable =
            node.value === 'x' || node.value === 'y' || node.value === 'z';
          return isVariable ? node.value : parseFloat(node.value).toFixed(4);
        }
        if (node.type === 'OPERATOR') {
          console.log('OPERATOR: ' + node.value);
          let left = traverseTree(node.left);
          let right = traverseTree(node.right);
  
          if (node.value === '^') {
            console.log(`pow(${left}, ${right})`);
            return `pow(${left}, ${right})`;
          } else {
            if (right && left) return `(${left})${node.value}(${right})`;
            else if (left) return `${node.value}(${left})`;
            else return '????';
          }
  
          console.log(node.toString());
          return node.toString();
        }
        if (node.type === 'FUNCTION') {
          console.log('FUNCTION: ' + node.value);
          let left = traverseTree(node.left);
          let right = traverseTree(node.right);
  
          if (node.value === '^') {
            console.log(`pow(${left}, ${right})`);
            return `pow(${left}, ${right})`;
          } else {
            if (right) return `${node.value}(${right})`;
            else return '????';
          }
        }
      }
    };
  
    const handleNewEquation = (newEq) => {
      let f = null;
      setEqInput(newEq);
      console.log("HANDLING NEW");
  
      try {
        f = nerdamer(newEq);
      } catch (error) {
        setErrorMsg(error.message);
        setValEq(false);
        console.log(error);
        console.warn('ERROR PARSING EQUATION');
  
        return;
      }
  
      setValEq(true);
      
      console.log("LUL");
      setErrorMsg('');
  
      let dfdx = nerdamer.diff(f, 'x', 1);
      let dfdy = nerdamer.diff(f, 'y', 1);
      let dfdz = nerdamer.diff(f, 'z', 1);
      let norm = nerdamer(`sqrt((${dfdx})^2 + (${dfdy})^2 + (${dfdz})^2)`);
  
      console.log('DEB 2');
      console.log(norm.toString());
  
      if (norm.toString() === '0') {
        setValEq(false);
        setErrorMsg("Norm can't be 0");
        return;
      }
  
      let sdf = nerdamer(`(${f})/(${norm})`);
      console.log('DEB 3');
      console.log('F: ' + f.toString());
      console.log('dFdX: ' + dfdx.toString());
      console.log('dFdY: ' + dfdy.toString());
      console.log('dFdZ: ' + dfdz.toString());
      console.log('norm: ' + norm.toString());
      console.log('sdf: ' + sdf.toString());
  
      var x = nerdamer.tree(sdf.toString());
  
      setEqData({
        f: f,
        dx: dfdx,
        dy: dfdy,
        dz: dfdz,
        norm: norm,
        sdf: sdf,
        parsedSdf: traverseTree(x),
      });
  
      console.log(eqData);
    };
  
    const handleSave = () => {
      console.log(storage);
      console.log(typeof storage);
  
      if (nameInput in storage) {
        console.log('ya esta');
      } else {
        let newData = { ...storage };
        console.log('old:');
        console.log(newData);
        newData[nameInput.toLowerCase()] = {
          id: nameInput.toLowerCase(),
          name: nameInput,
          implicit: eqData.f.toString(),
          sdf: eqData.sdf.toString(),
          parsedSdf: eqData.parsedSdf,
          fName: nameInput.toLowerCase(),
        };
        console.log('new:');
        console.log(newData);
        setStorage(newData);
        console.log('no esta');
        console.log(storage);
        props.handleClose();
      }
    };
  
    useEffect(() => {
      console.log("START");
      if(props.eqData){
        console.log(props.eqData);
        setNameInput(props.eqData.name);
        setEqInput(props.eqData.implicit);
        console.log("fff:" + props.eqData.implicit);
        handleNewEquation(props.eqData.implicit);
      }
      else{
        setNameInput("");
        setEqInput("");
      }
    }, [props.eqData]);
  
    useEffect(() => {
      console.log(storage);
      setValidName(!(nameInput in storage));
    }, [nameInput]);
  
    return (
      <Dialog open={props.open} onClose={props.handleClose}>
        <DialogTitle>New Surface</DialogTitle>
  
        <DialogContent>
          EQ: {eqData.f ? eqData.f.toString() : ''}
          <MathJax.Provider>
            <div>
              <p>Block formula:</p>
              <MathJax.Node
                formula={
                  eqData.f
                    ? `\\begin{align*} 
              f(x,y,z) &=  ${nerdamer.convertToLaTeX(
                eqData.f.toString()
              )} \\\\[10pt]
              \\nabla f(x,y,z) &= \\left( 
                ${nerdamer.convertToLaTeX(eqData.dx.toString())}, 
                ${nerdamer.convertToLaTeX(eqData.dy.toString())}, 
                ${nerdamer.convertToLaTeX(eqData.dz.toString())}
              \\right) \\\\[10pt]
              \\Vert \\nabla f(x,y,z) \\Vert &= ${nerdamer.convertToLaTeX(
                eqData.norm.toString()
              )} \\\\[10pt]
              \\text{sdf}(x,y,z) &= ${nerdamer.convertToLaTeX(
                eqData.sdf.toString()
              )}
            \\end{align*}`
                    : ''
                }
              />
              salida string: {parsedEq}
            </div>
          </MathJax.Provider>
          <DialogContentText>Introduce surface information:</DialogContentText>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
              value={nameInput}
                error={!validName || nameInput == ''}
                helperText={
                  nameInput === '' ? '' : validName ? '' : 'Name already in use'
                }
                onChange={(e) => setNameInput(e.target.value)}
                id="name"
                label="Name"
              />
            </Grid>
  
            <Grid item xs={12}>
              <TextField
                sx={{ width: '100%' }}
                value={eqInput}
                defaultValue=""
                label="Implicit"
                onChange={(e) => handleNewEquation(e.target.value)}
                id="Implicit"
                error={!validEq || eqInput === ''}
                helperText={errorMsg}
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end"> = 0</InputAdornment>
                  ),
                }}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={props.handleClose}>Cancel</Button>
          <Button
            onClick={handleSave}
            disabled={!validName || !validEq || nameInput === '' || eqInput === ''}
          >
            Save
          </Button>
        </DialogActions>
      </Dialog>
    );
  }
