import React from "react";
import { Handle } from "react-flow-renderer";
import { useTheme } from '@mui/material/styles';

const Triangle = ({ w = '20', h = '20', direction = 'right', color = 'white', borderColor="black" }) => {
  const points = {
    top: [`${w / 2},0`, `0,${h}`, `${w},${h}`],
    right: [`0,${-h/2}`, `${w},0`, `0,${h}`, ],
    bottom: [`0,0`, `${w},0`, `${w / 2},${h}`],
    left: [`${w},0`, `${w},${h}`, `0,${h / 2}`],
  }

  return (
    <svg width={2*w} height={2*h}>
      <polygon points={points[direction].join(' ')} stroke={borderColor} strokeWidth="1px" fill={color} />
      Sorry, your browser does not support inline SVG.
    </svg>
  )
}

export default function CustomHandle(props) {
  const theme = useTheme();
  const radius = 10;
  const margin = 2;

  const style = {
    ...props.style, width: `${radius}px`, height: `${radius}px`,
    background: `transparent`,
    borderWidth: "2px",
    border: `2px transparent ${theme.palette.primary.main}`
  }



  return (
    <>
      {props.type === "source" ?
        <Handle
          type="source"
          id={props.id}
          style={{
            ...style
          }}
          position="right"
        >
          hola
          <Triangle w={radius} h={radius} color={theme.palette.primary.light} borderColor={theme.palette.primary.main}/>
          </Handle>
        :
        <Handle
          type="target"
          id={props.id ? props.id : "0"}
          style={{
            ...style, marginLeft: `-${margin}px`
          }}
          position="left"
        />
      }
    </>
  );
}
