export const sphere = (radius) => `
    length(p)-${parseFloat(radius).toFixed(1)}
`;

export const box = (sx,sy,sz) => {
    let bx = parseFloat(sx).toFixed(1);
    let by = parseFloat(sy).toFixed(1);
    let bz = parseFloat(sz).toFixed(1);
    return `
        length( 
            max(
                vec3(abs(p.x)-${bx}, abs(p.y)-${by}, abs(p.z)-${bz}), 
                0.0
            ) 
        ) 
        + 
        min(
            max( 
                abs(p.x)-${bx}, 
                max(abs(p.y)-${by}, abs(p.z)-${bz}) 
            ),
            0.0
        )
    `
};
