import nerdamer from "nerdamer";

export default class Polynomial {
    pol = "0";
    lc = "0";

    constructor(p){
        try{
            this.pol = nerdamer(p).simplify().toString();
        }catch(e){
            console.log(`ERROR PARSING POLYNOMIAL ${p}`);
        }

        console.log(this.pol);
        this.traverseTree(nerdamer.tree(this.pol));
    }

    nodeToString(node){
        if(node){
            let left = this.traverseTree(node.left);
            let right = this.traverseTree(node.right);

            return `${node.left ? node.left : ''}${node.value}${node.right ? node.right : ''}`;
        }
    }

    traverseTree(node) {
        console.log(this.nodeToString(node));
        if (node) {
          
          if (node.type === 'VARIABLE_OR_LITERAL') {
            const isVariable = ['x', 'y', 'z'].includes(node.value);
            console.log("ES LITERAL");
            console.log("SYMBOLS");
            console.log(node.value);
            return isVariable ? node.value : parseFloat(node.value).toFixed(4);
          }
          if (node.type === 'OPERATOR') {
            console.log("left");
            let left = this.traverseTree(node.left);
            let right = this.traverseTree(node.right);
            console.log("RIGHT, LEFT");
            console.log(right);
            console.log(left);
            console.log("ES OPERATOR");
    
            if (node.value === '^') {
              
              console.log(node);
              return `pow(${left}, ${right})`;
            } else {
              if (right && left) return `(${left})${node.value}(${right})`;
              else if (left) return `${node.value}(${left})`;
              else return '????';
            }
    
            // return node.toString();
          }
          if (node.type === 'FUNCTION') {
            let left = this.traverseTree(node.left);
            let right = this.traverseTree(node.right);
            console.log("ES F");
            if (node.value === '^') {
              
              console.log(node);
              return `pow(${left}, ${right})`;
            } else {
              if (right) return `${node.value}(${right})`;
              else return '????';
            }
          }
        }
      };
}
