import nerdamer from "nerdamer";
import { isGetAccessor } from "typescript";

export default class Polynomial {
    pol = "0";
    lc = "0";

    constructor(p){
        try{
            this.pol = nerdamer(p).simplify().toString();
        }catch(e){
            console.log(`ERROR PARSING POLYNOMIAL ${p}`);
        }

        console.log(this.pol);
        // this.traverseTree(nerdamer.tree(this.pol));
        console.log("CHECK STRING 2*x + y -z")
        this.nodeToString(nerdamer.tree('2*x + y - z'));

        console.log("CHECK STRING 2*x^2 + x*y -z*y + 2*x*y")
        this.nodeToString(nerdamer.tree('2*x^2 + x*y -z*y + 2*x*y'));

        console.log("CHECK STRING (2+4)*x + (x+y)*z")
        this.nodeToString(nerdamer.tree('(2+4)*x + (x+y)*z'));
    }

    nodeToString(node){
        if(node){
            let left = this.traverseTree(node.left);
            let right = this.traverseTree(node.right);
            // console.log(node);
            // console.log("LEFT: " + left);
            // console.log("RIGHT: " + right);

            console.log(`DEVOLVEMOS ${node.type==="OPERATOR" ? `${node.value}(${left})`: `${left}${node.value}${right}`}`)


            if(node.type === "OPERATOR"){
                if(right && left)   return `(${left})${node.value}(${right})`;
                else                return `${node.value}(${left})`;
            }    
            else                            
                return `${left}${node.value}${right}`
        }
    }

    traverseTree(node) {
        // console.log(node);
        if (node!==null && node!==undefined) {

          if (node.type === 'VARIABLE_OR_LITERAL') {
            const isVariable = ['x', 'y', 'z'].includes(node.value);
            // console.log("ES LITERAL O VARIABLE: " + node.value);
            // console.log(node.value);
            return isVariable ? node.value : parseFloat(node.value).toFixed(4);
          }

          if (node.type === 'OPERATOR') {
            
            let left = this.traverseTree(node.left);
            let right = this.traverseTree(node.right);

            // console.log("ES OPERATOR: " + node.value);
            // console.log("OPERATOR LEFT: " + left);
            // console.log("OPERATOR RIGHT: " + right);
            // console.log(`DEVUELVO ${(right && left) ? `${left}${node.value}${right}` : `${node.value}${left}`}`);

            if(right && left)   return `${left}${node.value}${right}`;
            else if(right)      return `${node.value}(${right})`
            else                return `${node.value}(${left})`;
          }

          if (node.type === 'FUNCTION') {
            // console.log("ES F: " + node.value);
            let left = this.traverseTree(node.left);
            let right = this.traverseTree(node.right);
            
            // console.log(`DEVUELVO ${left}${node.value}${right}`);
            return `${left}${node.value}${right}`;
            
          }
        }

        return '';
      };
}
